```ts ghostty-config/src/lib/utils/themeHandler.ts
import type {HexColor} from "$lib/utils/colors";
import config from "$lib/stores/config.svelte";
import {fetchColorScheme, parseColorScheme} from "$lib/data/settings";

export default async function themeHandler(themeName: string) {
    const {colorSchemeResponse} = await fetchColorScheme(themeName);
    const colorScheme = parseColorScheme(colorSchemeResponse);

    const colorProperties = [
        "background",
        "foreground",
        "selectionBackground",
        "selectionForeground",
        "cursorColor"
    ] as const;

    colorProperties.forEach((prop) => {
        if (colorScheme[prop]) config[prop] = colorScheme[prop] as HexColor;
    });

    if (colorScheme.palette) {
        config.palette = Array(256).fill("#000000") as HexColor[];
        for (const [index, color] of Object.entries(colorScheme.palette)) {
            config.palette[+index] = color as HexColor;
        }
    }
}
