interface BaseSettingType {
    id: string;
    name: string;
    note?: string;
}

interface Panel extends BaseSettingType {
    groups: Group[];
}

interface Group extends BaseSettingType {
    settings: (Switch | Text | Number | Dropdown | Color | Palette | Keybinds)[];
    // type: "group";
}

type SettingType = "switch" | "number" | "dropdown" | "text" | "color" | "palette" | "keybinds";

interface BaseSettingItem extends BaseSettingType {
    type: SettingType;
    value: unknown;
}

interface Switch extends BaseSettingItem {
    type: "switch";
    value: boolean;
}

interface Text extends BaseSettingItem {
    type: "text";
    value: string;
}

interface Number extends BaseSettingItem {
    type: "number";
    value: number;
    min?: number;
    max?: number;
    step?: number;
    size?: number;
    range?: boolean;
}

interface DropdownOption {
    name: string;
    value: string;
}

interface Dropdown extends BaseSettingItem {
    type: "dropdown";
    value: "string";
    options: (DropdownOption | string)[];
}

interface Color extends BaseSettingItem {
    value: HexColor;
    type: "color";
}

interface Palette extends BaseSettingItem {
    value: HexColor[];
    type: "palette";
}

export type KeybindString = `${string}=${string}`;
interface Keybinds extends BaseSettingItem {
    value: KeybindString[];
    type: "keybinds";
}

interface ThemeResponse {
  "type": string;
  "encoding": string;
  "size": number;
  "name": string;
  "path": string;
  "content": string;
  "sha": string;
  "url": string;
  "git_url": string;
  "html_url": string;
  "download_url": string;
  "_links": {
    "git": string;
    "self": string;
    "html": string;
  }
}

interface ColorScheme {
  palette: { [key: string]: string };
  background?: string;
  foreground?: string;
  cursorColor?: string;
  selectionBackground?: string;
  selectionForeground?: string;
}

const fetchThemeFiles = async () => {
    const apiUrl = "https://api.github.com/repos/mbadolato/iTerm2-Color-Schemes/contents/ghostty";

    const response = await fetch(apiUrl);

    if (!response.ok) {
        throw new Error(`Error fetching data: ${response.statusText}`);
    }

    const themeFiles = await response.json();

    return {themeFiles};
};

// Function to convert raw string data into a JSON object
export const parseColorScheme = (input: string): ColorScheme => {
  const colorScheme: ColorScheme = {palette: {}};

  // Split input by lines
  const lines = input.split("\n");

  lines.forEach((line) => {
    if (line.startsWith("palette")) {
      const [, rest] = line.split("palette =").map(part => part.trim());

      if (rest) {
        const [paletteIndex, color] = rest.split("=");
        if (paletteIndex && color) {
          colorScheme.palette[paletteIndex.trim()] = color.trim();
        }
      }
    }
    else {
      const [key, value] = line.split("=").map(part => part.trim());

      switch (key) {
        case "background":
          colorScheme.background = `#${value}`;
          break;
        case "foreground":
          colorScheme.foreground = `#${value}`;
          break;
        case "cursor-color":
          colorScheme.cursorColor = `#${value}`;
          break;
        case "selection-background":
          colorScheme.selectionBackground = `#${value}`;
          break;
        case "selection-foreground":
          colorScheme.selectionForeground = `#${value}`;
          break;
        default:
          break;
      }
    }
  });

  return colorScheme;
};

export const fetchColorScheme = async (theme: string) => {
    const apiUrl = `https://api.github.com/repos/mbadolato/iTerm2-Color-Schemes/contents/ghostty${theme ? "/" + theme : ""}`;

    const response = await fetch(apiUrl,{
        headers: {
            Accept: "application/vnd.github.raw+json"
        }
    });

    if (!response.ok) {
        throw new Error(`Error fetching data: ${response.statusText}`);
    }

    const colorSchemeResponse = await response.text();

    return {colorSchemeResponse};
};

