```ts ghostty-config/src/lib/utils/themeHandler.ts
/* eslint-disable no-console */
import type {HexColor} from "$lib/utils/colors";
import config from "$lib/stores/config.svelte";
import {fetchColorScheme, parseColorScheme} from "$lib/data/settings";

export default async function themeHandler(themeName: string) {
    try {
        console.log("Applying theme:", themeName);
        const {colorSchemeResponse} = await fetchColorScheme(themeName);
        const colorScheme = parseColorScheme(colorSchemeResponse);

        console.log("Parsed color scheme:", colorScheme);

        const colorProperties = [
            "background",
            "foreground",
            "selectionBackground",
            "selectionForeground",
            "cursorColor"
        ] as const;

        colorProperties.forEach((prop) => {
            if (colorScheme[prop]) {
                config[prop] = colorScheme[prop] as HexColor;
                console.log(`Updated ${prop}:`, config[prop]);
            }
        });

        if (colorScheme.palette) {
            const newPalette = Array(256).fill("#000000") as HexColor[];
            for (const [index, color] of Object.entries(colorScheme.palette)) {
                newPalette[+index] = color as HexColor;
            }
            config.palette = [...newPalette]; // Create a new array reference to trigger reactivity
            console.log("Updated palette:", config.palette);
        }

        // Force a re-render by updating the theme property
        config.theme = themeName;
        console.log("Theme application complete");

        // Dispatch a custom event to notify that the theme has been applied
        window.dispatchEvent(new CustomEvent("themeApplied", {detail: {theme: themeName}}));
    } catch (error) {
        console.error("Error applying theme:", error);
    }
}
